mapper.update:
update member
set password=#{password}
where username=#{username} and enabled = 1

update auth
set code=#{code}
where username=#{username}

update member
set nickname=#{nickname}, password=#{password}, email=#{email}
where username=#{username}

update member
set enabled=0
where username=#{username}

mapper.select:
 select * from
 (
   select a.*,rownum rnum from
   (
	   select * from member
	   
	   order by regdate desc
   )a
 )
  where rnum>=#{startRow} and rnum<=#{endRow}

	select * from
 	(
		select a.*,rownum rnum from
	    (
			select * from point where username=#{username}
			
				and  score > 0 
			
			
				and  score < 0 
			
			order by regdate desc
		)a
	)
	 where rnum>=#{startRow} and rnum<=#{endRow}

 select * from
 (
   select a.*,rownum rwnum from
   (
	   select * from report
	   order by rNum desc
   )a
 )
  where rwnum>=#{startRow} and rwnum<=#{endRow}

select * from
 (
   select a.*,rownum rnum from
   (
	   select * from message
	   where receiver = #{username}
	   order by msgNum desc
   )a
 )
  where rnum>=#{startRow} and rnum<=#{endRow}

select * from
 (
   select a.*,rownum rnum from
   (
	   select * from community where pNum In
	   (
	   		select pNUm from scrap where username = #{username}
	   )
   )a
 )
  where rnum>=#{startRow} and rnum<=#{endRow}

mapper.update:
update member
set snickname = #{snickname}
where username = #{username}
